package beginJava;

public class IfCode {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		
		int num1 = 100;
		int num2 = 100;
		
		if(num1 < num2) {
			System.out.println("Num1 is less than Num2");
		}
		else if(num1 > num2) {
			System.out.println("Num1 is greater than Num2");
		}
		else {
			System.out.println("Num1 is Equal to Num2");
		}
	}
}
